mapper.update:
update message
set is_deleted=1
where receiver_id = #{userId}
  and `read` = 1
  and is_deleted = 0;

mapper.select:
select *
from message
where
receiver_id=#{userId}

    AND id > #{lastId}

and is_deleted=0
order by `read`
limit

    #{pageFrom},

#{pageSize}

select count(*)
from message
where receiver_id = #{userId}
  and is_deleted = 0;
